function generateChapter(chapterNum, versesArray) {
  const varName = `johnChapter${chapterNum}UK`;
  
  const verses = versesArray.map((text, index) => ({
    number: index + 1,
    text: text,
    strong: []
  }));
  
  const data = {
    chapter: chapterNum,
    title: `Jean ${chapterNum}`,
    version: "Ukrainian Bible 1962",
    language: "uk",
    direction: "ltr",
    verses: verses
  };
  
  const content = `// ============================================================================
// √âVANGILE DE JEAN - Ukrainian Bible (–£–∫—Ä–∞—ó–Ω—Å—å–∫–∞ –ë—ñ–±–ª—ñ—è)
// ============================================================================
// Chapitre ${chapterNum}

export const ${varName} = ${JSON.stringify(data, null, 2)};

export default ${varName};
`;
  
  return content;
}

