"use strict";(self.webpackChunkunityquest_chronicles_of_love=self.webpackChunkunityquest_chronicles_of_love||[]).push([[4679],{42583:(a,t,n)=>{n.r(t),n.d(t,{default:()=>i,johnChapter5SW:()=>e});const e={chapter:5,title:"Jean 5",version:"Swahili Union Version",language:"sw",direction:"ltr",verses:[{number:1,text:"Baada ya mambo hayo kulikuwa na sikukuu ya Wayahudi; Yesu akapanda kwenda Yerusalemu.",strong:[]},{number:2,text:"Basi huko Yerusalemu kuna bwawa karibu na lango la Kondoo, linaloitwa kwa Kiebrania Bethesda, likiwa na baraza tano.",strong:[{text:"brebis",strong:"G4263",start:38,end:44}]},{number:3,text:"Ndani yake walikuwa wamelala umati wa wagonjwa, vipofu, vilema, wenye kukauka, wakingoja maji kugharakazwa.",strong:[{text:"grand",strong:"G3173",start:38,end:43}]},{number:4,text:"Maana malaika alikuwa anashuka wakati wake bwawani, akayagharakaza maji; basi yule aliyeingia wa kwanza baada ya kugharakazwa kwa maji alipona ile maradhi iliyokuwa imemshika.",strong:[{text:"ange",strong:"G32",start:7,end:11},{text:"dans",strong:"H6887",start:41,end:45},{text:"premier",strong:"G4413",start:105,end:112}]},{number:5,text:"Na mtu mmoja alikuwako pale, ambaye alikuwa na maradhi miaka thelathini na minane.",strong:[{text:"homme",strong:"G444",start:18,end:23},{text:"malade",strong:"G772",start:24,end:30}]},{number:6,text:"Yesu alipomwona huyu amelala, naye akajua ya kuwa amekuwa hivyo muda mrefu, akamwambia, Je, unataka kupona?",strong:[{text:"malade",strong:"G772",start:49,end:55}]},{number:7,text:"Yule mgonjwa akamjibu, Bwana, sina mtu wa kunishushia bwawani maji yanapogharakazwa; bali ninapoenda mimi, mwingine hutangulia kushuka kabla yangu.",strong:[{text:"malade",strong:"G772",start:3,end:9},{text:"Seigneur",strong:"G2962",start:24,end:32},{text:"personne",strong:"G3762",start:42,end:50},{text:"dans",strong:"H6887",start:65,end:69}]},{number:8,text:"Yesu akamwambia, Inuka, chukua kitanda chako, uende zako.",strong:[]},{number:9,text:"Mara yule mtu akapona, akachukua kitanda chake, akaenda zake. Siku hiyo ilikuwa Sabato.",strong:[{text:"homme",strong:"G444",start:13,end:18}]},{number:10,text:"Basi Wayahudi wakamwambia yule aliyeponywa, Leo ni Sabato; hukupasi wewe kubeba kitanda.",strong:[{text:"jour",strong:"H3117",start:11,end:15}]},{number:11,text:"Akawajibu, Yule aliyeniponya, ndiye aliyeniambia, Chukua kitanda chako, uende zako.",strong:[]},{number:12,text:"Wakamwuliza, Ni yupi yule mtu aliyekuambia, Chukua kitanda chako, uende zako?",strong:[{text:"homme",strong:"G444",start:31,end:36}]},{number:13,text:"Lakini yule aliyeponywa hakujua yeye ni nani; maana Yesu alikuwa amejitenga, kwa kuwa umati ulikuwa mahali pale.",strong:[{text:"foule",strong:"G3793",start:88,end:93}]},{number:14,text:"Baada ya mambo hayo Yesu akamkuta hekaluni, akamwambia, Tazama, umepona; usifanye dhambi tena, isiwe jambo baya zaidi likakupata.",strong:[{text:"dans",strong:"H6887",start:24,end:28},{text:"peur",strong:"H3372",start:94,end:98}]},{number:15,text:"Yule mtu akaenda, akawaambia Wayahudi ya kuwa ni Yesu aliyemponya.",strong:[{text:"homme",strong:"G444",start:4,end:9}]},{number:16,text:"Na kwa sababu hiyo Wayahudi wakamtesa Yesu, kwa sababu alifanya mambo hayo siku ya Sabato.",strong:[{text:"jour",strong:"H3117",start:80,end:84}]},{number:17,text:"Lakini Yesu akawajibu, Baba yangu anafanya kazi mpaka sasa, nami nafanya kazi.",strong:[]},{number:18,text:"Kwa sababu hiyo basi Wayahudi wakazidi kutafuta kumwua, kwa sababu si kuvunja tu Sabato, bali pia kusema Mungu ni Baba yake, akajifanya sawa na Mungu.",strong:[{text:"faire",strong:"G1080",start:56,end:61},{text:"Dieu",strong:"G2316",start:141,end:145},{text:"propre",strong:"G2398",start:150,end:156},{text:"Dieu",strong:"G2316",start:190,end:194}]},{number:19,text:"Basi Yesu akajibu, akawaambia, Amin, amin, nawaambia, Mwana hawezi kufanya cho chote kwa nafsi yake, isipokuwa alionalo Baba akifanya; maana mambo yote afanyayo yeye, Mwana naye huyafanya vivyo hivyo.",strong:[{text:"parole",strong:"G3056",start:21,end:27},{text:"Fils",strong:"G5207",start:83,end:87},{text:"rien",strong:"G3762",start:96,end:100},{text:"faire",strong:"G1080",start:101,end:106},{text:"faire",strong:"G1080",start:149,end:154},{text:"tout",strong:"G3956",start:167,end:171},{text:"Fils",strong:"G5207",start:196,end:200}]},{number:20,text:"Maana Baba anampenda Mwana, anamwonyesha mambo yote ayafanyayo yeye mwenyewe; naye atamwonyesha matendo makuu kuliko haya, ili ninyi mstaajabu.",strong:[{text:"Fils",strong:"G5207",start:20,end:24},{text:"tout",strong:"G3956",start:40,end:44},{text:"dans",strong:"H6887",start:138,end:142}]},{number:21,text:"Maana kama vile Baba afufuavyo wafu, na kuwapa uzima, ndivyo naye Mwana awapavyo uzima wao awatakao.",strong:[{text:"comme",strong:"G2531",start:5,end:10},{text:"Fils",strong:"G5207",start:66,end:70}]},{number:22,text:"Maana Baba hahukumu mtu; bali amempa Mwana hukumu yote;",strong:[{text:"personne",strong:"G3762",start:16,end:24},{text:"tout",strong:"G3956",start:42,end:46},{text:"jugement",strong:"G2920",start:47,end:55},{text:"Fils",strong:"G5207",start:59,end:63}]},{number:23,text:"ili wote wamheshimu Mwana, kama vile wanavyomheshimu Baba. Yeye asiyemheshimu Mwana hamheshimu Baba aliyemtuma yeye.",strong:[{text:"Fils",strong:"G5207",start:26,end:30},{text:"comme",strong:"G2531",start:31,end:36},{text:"Fils",strong:"G5207",start:85,end:89}]},{number:24,text:"Amin, amin, nawaambia, Yeye alisikiaye neno langu, na kumwamini yeye aliyenituma, ana uzima wa milele, wala hajaingia hukumuni, bali amekwisha kupita kutoka mautini mpaka uzimani.",strong:[{text:"parole",strong:"G3056",start:58,end:64},{text:"jugement",strong:"G2920",start:143,end:151},{text:"mort",strong:"H6757",start:177,end:181}]},{number:25,text:"Amin, amin, nawaambia, Wakati unakuja, naam, umefika sasa, ambapo wafu watasikia sauti ya Mwana wa Mungu; nao waisikiao wataishi.",strong:[{text:"Fils",strong:"G5207",start:112,end:116},{text:"Dieu",strong:"G2316",start:120,end:124}]},{number:26,text:"Maana kama vile Baba alivyo na uzima ndani yake mwenyewe, ndivyo alivyo naye amempa Mwana kuwa na uzima ndani yake mwenyewe;",strong:[{text:"comme",strong:"G2531",start:5,end:10},{text:"Fils",strong:"G5207",start:61,end:65},{text:"avoir",strong:"G1372",start:68,end:73}]},{number:27,text:"tena amempa mamlaka ya kuhukumu, kwa sababu yeye ni Mwana wa Adamu.",strong:[{text:"juger",strong:"G2919",start:32,end:37},{text:"Fils",strong:"G5207",start:55,end:59},{text:"homme",strong:"G444",start:65,end:70}]},{number:28,text:"Msistaajabu jambo hili; maana wakati unakuja, ambapo wote walio makaburini watasikia sauti yake,",strong:[{text:"dans",strong:"H6887",start:69,end:73}]},{number:29,text:"nao watatoka; wale waliotenda mema, kwa ufufuo wa uzima; na wale waliotenda mabaya, kwa ufufuo wa hukumu.",strong:[{text:"bien",strong:"H2896",start:24,end:28},{text:"jugement",strong:"G2920",start:113,end:121}]},{number:30,text:"Mimi siwezi kufanya kitu cho chote kwa nafsi yangu; kama nisikiavyo, nahukumu; na hukumu yangu ni ya haki; kwa sababu sitafuti mapenzi yangu mwenyewe, bali mapenzi yake yeye aliyenituma.",strong:[{text:"rien",strong:"G3762",start:11,end:15},{text:"faire",strong:"G1080",start:16,end:21},{text:"selon",strong:"G2531",start:35,end:40},{text:"jugement",strong:"G2920",start:72,end:80}]},{number:31,text:"Kama mimi nikishuhudia habari zangu, ushuhuda wangu si wa kweli.",strong:[]},{number:32,text:"Yupo mwingine ashuhudiaye habari zangu; nami najua ya kuwa ushuhuda wake, aushuhudiaye habari zangu, ni wa kweli.",strong:[]},{number:33,text:"Ninyi mlimtumia Yohana, naye ameshuhudia habari za kweli.",strong:[{text:"Jean",strong:"G2491",start:22,end:26}]},{number:34,text:"Lakini mimi sipokei ushuhuda kutoka kwa mtu; hata hivyo nasema haya, ili ninyi mwokolewe.",strong:[{text:"homme",strong:"G444",start:28,end:33},{text:"ceci",strong:"G3778",start:75,end:79}]},{number:35,text:"Yeye alikuwa taa iwakayo na ing'aayo; nanyi mkakubali kufurahi kwa kitambo katika nuru yake.",strong:[{text:"Jean",strong:"G2491",start:0,end:4}]},{number:36,text:"Lakini mimi nina ushuhuda mkuu kuliko ule wa Yohana; maana kazi alizonipatia Baba kuikamilisha, kazi hii hii ninayoifanya, inashuhudia habari zangu, ya kuwa Baba amenituma.",strong:[{text:"grand",strong:"G3173",start:29,end:34},{text:"Jean",strong:"G2491",start:48,end:52},{text:"accomplir",strong:"G4160",start:93,end:102}]},{number:37,text:"Na Baba aliyenituma, yeye mwenyewe ameshuhudia habari zangu. Hamkusikia sauti yake kamwe, wala hamkuuona umbo lake;",strong:[]},{number:38,text:"wala neno lake halikubaki ndani yenu; maana yeye aliyemtuma, huyo hamumwamini ninyi.",strong:[{text:"parole",strong:"G3056",start:6,end:12},{text:"demeure",strong:"H1004",start:16,end:23}]},{number:39,text:"Mnapayachunguza maandiko, kwa sababu mnadhania ya kuwa ndani yake mna uzima wa milele; nayo ni hayo yashuhudiayo habari zangu;",strong:[{text:"avoir",strong:"G1372",start:49,end:54}]},{number:40,text:"nanyi hamtaki kuja kwangu, ili mupate uzima.",strong:[{text:"avoir",strong:"G1372",start:39,end:44}]},{number:41,text:"Sipokei utukufu utokao kwa wanadamu.",strong:[]},{number:42,text:"Lakini ninyi nawajua, ya kuwa hamna upendo wa Mungu ndani yenu.",strong:[{text:"amour",strong:"G25",start:45,end:50},{text:"Dieu",strong:"G2316",start:54,end:58}]},{number:43,text:"Mimi nimekuja kwa jina la Baba yangu, nanyi hamnipokeeni; mtu mwingine akija kwa jina lake mwenyewe, huyo mtampokea.",strong:[{text:"propre",strong:"G2398",start:85,end:91}]},{number:44,text:"Ninyi mnawezaje kuamini, mkipokeana utukufu mnaopokeana ninyi kwa ninyi, wala hamtafuti utukufu utokao kwa yule Mungu pekee?",strong:[{text:"croire",strong:"G4100",start:20,end:26},{text:"Dieu",strong:"G2316",start:124,end:128},{text:"seul",strong:"G3439",start:129,end:133}]},{number:45,text:"Msifikiri ya kuwa mimi nitawashitaki mbele za Baba; yupo awashitakiye ninyi, Mose, mliyemtumainia yeye.",strong:[]},{number:46,text:"Maana kama mngalimwamini Mose, mngalinamini mimi; kwa maana yeye aliandika habari zangu.",strong:[]},{number:47,text:"Lakini kama hamyaamini maandiko yake yeye, mtayaamini vipi maneno yangu?",strong:[]}]},i=e}}]);
//# sourceMappingURL=john-john-05-sw-js.ed003dac.chunk.js.map