"use strict";(self.webpackChunkunityquest_chronicles_of_love=self.webpackChunkunityquest_chronicles_of_love||[]).push([[9479],{2471:(a,n,t)=>{t.r(n),t.d(n,{default:()=>i,johnChapter16SW:()=>e});const e={chapter:16,title:"Jean 16",version:"Swahili Union Version",language:"sw",direction:"ltr",verses:[{number:1,text:"Mambo haya nimewaambia, ili msije mkajikwaa.",strong:[]},{number:2,text:"Watawafukuza nje ya masinagogi; naam, wakati unakuja, kwamba kila mtu awauaye ninyi atadhani ya kuwa humtumikie Mungu.",strong:[{text:"rendre",strong:"G1743",start:94,end:100},{text:"Dieu",strong:"G2316",start:112,end:116}]},{number:3,text:"Nao watafanya mambo haya, kwa sababu hawakumjua Baba, wala mimi.",strong:[]},{number:4,text:"Lakini mambo haya nimewaambia, ili wakati wake utakapofika, mkumbuke ya kuwa mimi niliwaambia. Wala sikuwaambia mambo haya tangu mwanzo, kwa sababu nilikuwa pamoja nanyi.",strong:[{text:"commencement",strong:"G746",start:145,end:157},{text:"avec",strong:"G1848",start:177,end:181}]},{number:5,text:"Lakini sasa ninamwendea yeye aliyenituma; wala hakuna yeyote wenu aniuliziaye, Unaenda wapi?",strong:[{text:"aucun",strong:"G3762",start:54,end:59}]},{number:6,text:"Bali kwa sababu nimewaambia mambo haya, huzuni imejaza mioyo yenu.",strong:[]},{number:7,text:"Hata hivyo ninawaambia kweli; ni afadhali kwenu niondoke; maana nisipoondoka, yule Mfariji hatawaendea; lakini nikiondoka, nitamtuma kwenu.",strong:[]},{number:8,text:"Naye atakapokuja, atauonya ulimwengu habari za dhambi, na habari za haki, na habari za hukumu;",strong:[{text:"monde",strong:"G2889",start:40,end:45},{text:"justice",strong:"G2920",start:78,end:85},{text:"jugement",strong:"G2920",start:93,end:101}]},{number:9,text:"habari za dhambi, kwa sababu hawaiamini mimi;",strong:[]},{number:10,text:"na habari za haki, kwa sababu ninamwendea Baba, wala hamtaniona tena;",strong:[{text:"justice",strong:"G2920",start:3,end:10}]},{number:11,text:"na habari za hukumu, kwa sababu mkuu wa ulimwengu huu amehukumiwa.",strong:[{text:"jugement",strong:"G2920",start:3,end:11},{text:"monde",strong:"G2889",start:39,end:44}]},{number:12,text:"Bado nina mambo mengi ya kuwaambia, lakini hamwezi kuyachukua sasa.",strong:[{text:"beaucoup",strong:"G4183",start:12,end:20},{text:"dire",strong:"H559",start:38,end:42}]},{number:13,text:"Hata hivyo yeye, Roho wa kweli, atakapokuja, atawaongozeni katika kweli yote; maana hatazungumza kwa mamlaka yake mwenyewe, bali atazungumza yasikiayo yote; naye atawaambieni mambo yajayo.",strong:[{text:"Esprit",strong:"G4151",start:34,end:40},{text:"dans",strong:"H6887",start:69,end:73},{text:"tout",strong:"G3956",start:139,end:143}]},{number:14,text:"Yeye ataniutukuza mimi; maana atapokea yangu, akawatangazie ninyi.",strong:[]},{number:15,text:"Vyote alivyo navyo Baba ni vyangu; kwa sababu hiyo nilisema, Atapokea yangu, akawatangazie ninyi.",strong:[{text:"Tout",strong:"G3956",start:0,end:4}]},{number:16,text:"Kitambo kidogo, wala hamtaniona; na tena kitambo kidogo, nanyi mtaniona.",strong:[]},{number:17,text:"Basi wengine wa wanafunzi wake wakasemezana, Ni nini hiki anachosema nasi, Kitambo kidogo, wala hamtaniona; na tena kitambo kidogo, nanyi mtaniona; na, Kwa sababu ninamwendea Baba?",strong:[]},{number:18,text:"Basi wakasema, Ni nini hicho asemacho, Kitambo kidogo? Hatujui asemacho.",strong:[]},{number:19,text:"Yesu akajua ya kuwa walitaka kumwuliza, akawaambia, Je, mnabishana juu ya hili, kwa sababu nilisema, Kitambo kidogo, wala hamtaniona; na tena kitambo kidogo, nanyi mtaniona?",strong:[{text:"interroger",strong:"G2065",start:33,end:43}]},{number:20,text:"Amin, amin, nawaambia, Ninyi mtalia na kuomboleza, bali ulimwengu utafurahi; nanyi mtahuzunika, lakini huzuni yenu itageuzwa kuwa furaha.",strong:[{text:"monde",strong:"G2889",start:84,end:89},{text:"dans",strong:"H6887",start:114,end:118}]},{number:21,text:"Mwanamke anapozaa ana huzuni, kwa sababu saa yake imefika; lakini aishazapo mtoto, hafukumbi tena ile dhiki, kwa sababu ya furaha kwamba mtu amezaliwa ulimwenguni.",strong:[{text:"jour",strong:"H3117",start:116,end:120},{text:"enfant",strong:"G5040",start:125,end:131},{text:"homme",strong:"G444",start:217,end:222},{text:"dans",strong:"H6887",start:230,end:234},{text:"monde",strong:"G2889",start:238,end:243}]},{number:22,text:"Nanyi basi sasa mnayo huzuni; lakini nitawaona tena, na mioyo yenu itafurahi, wala hakuna mtu atakayewanyang\u02bcanyeni furaha yenu.",strong:[{text:"dans",strong:"H6887",start:38,end:42}]},{number:23,text:"Na siku ile hamtaniuliza mimi cho chote. Amin, amin, nawaambia, Cho chote mtakachomwomba Baba kwa jina langu, atawapeni.",strong:[{text:"jour",strong:"H3117",start:6,end:10},{text:"rien",strong:"G3762",start:47,end:51}]},{number:24,text:"Mpaka sasa hamkuomba cho chote kwa jina langu; ombeni, nanyi mtapokea, ili furaha yenu itimie.",strong:[{text:"rien",strong:"G3762",start:28,end:32}]},{number:25,text:"Mambo haya nimewaambia kwa mafumbo; wakati unakuja, ambapo sitasema tena nanyi kwa mafumbo, bali nitawatangazie wazi habari za Baba.",strong:[]},{number:26,text:"Siku ile mtaomba kwa jina langu; wala sisemi kwamba mimi nitamwombeni Baba kwa ajili yenu;",strong:[{text:"jour",strong:"H3117",start:6,end:10}]},{number:27,text:"maana Baba mwenyewe anawapenda ninyi, kwa sababu ninyi mmenipenda mimi, na kusadiki ya kuwa mimi nilitoka kwa Mungu.",strong:[{text:"Dieu",strong:"G2316",start:102,end:106}]},{number:28,text:"Nilitoka kwa Baba, nimekuja ulimwenguni; tena ninauwachaa ulimwengu, ninamwendea Baba.",strong:[{text:"dans",strong:"H6887",start:39,end:43},{text:"monde",strong:"G2889",start:47,end:52},{text:"monde",strong:"G2889",start:78,end:83}]},{number:29,text:"Wanafunzi wake wakamwambia, Tazama, sasa unasema wazi, wala husemi fumbo lo lote.",strong:[]},{number:30,text:"Sasa tunajua ya kuwa unajua mambo yote, wala hauhitaji mtu kukuuliza; kwa hili tunaamini ya kuwa ulitoka kwa Mungu.",strong:[{text:"besoin",strong:"H2637",start:69,end:75},{text:"personne",strong:"G3762",start:80,end:88},{text:"Dieu",strong:"G2316",start:149,end:153}]},{number:31,text:"Yesu akawajibu, Sasa mnaamini?",strong:[]},{number:32,text:"Tazama, saa inakuja, naam, imekwisha kufika, mtatawanyika kila mtu mahali pake, mkaniacha peke yangu; wala si peke yangu, kwa sababu Baba yu pamoja nami.",strong:[{text:"seul",strong:"G3439",start:114,end:118},{text:"seul",strong:"G3439",start:140,end:144},{text:"avec",strong:"G1848",start:162,end:166}]},{number:33,text:"Mambo haya nimewaambia, ili mkawa na amani ndani yangu. Ulimwenguni mtakuwa na dhiki; lakini jipeni moyo; mimi nimeushinda ulimwengu.",strong:[{text:"paix",strong:"G1515",start:49,end:53},{text:"dans",strong:"H6887",start:90,end:94},{text:"monde",strong:"G2889",start:98,end:103},{text:"monde",strong:"G2889",start:141,end:146}]}]},i=e}}]);
